import React from 'react'
import { useState } from 'react';
export const Timer = () => {
    const[hour,setHour]= useState(0);
    const[min,setMin]= useState(0);
    const[sec,setSec]= useState(0);


  return (
    <div>
        <div>
        <input type="number" value={hour} placeholder="00 Hr" onChange={(e)=>setHour(e.target.value)}/>
        <input type="number" value={min}  placeholder="00 Min" onChange={(e)=>setHour(e.target.value)}/>
        <input type="number" value={sec}  placeholder="00 Sec" onChange={(e)=>setHour(e.target.value)}/>
        </div>
        <div>
        <button onClick={handleStart}>START</button>
        <button  onClick={handleStop}>STOP</button>
        <button  onClick={handleReset}>RESET</button>
        </div>
    </div>
  )
}
